import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import styles from './Ticket.module.css';

const Booking = () => {
  const [selectedDate, setSelectedDate] = useState(null);

  const handleDateChange = event => {
    const selectedValue = event.target.value;
    setSelectedDate(selectedValue !== 'none' ? selectedValue : null);
  };

  // Tính toán ngày hiện tại
  const currentDate = new Date();

  // Tính toán danh sách 7 ngày
  const dateOptions = Array.from({ length: 7 }, (_, index) => {
    const date = new Date(currentDate);
    date.setDate(currentDate.getDate() + index);
    return date.toLocaleDateString();
  });

  return (
    <section className={styles.session}>
      <div>
        <h2>Chọn ngày đặt vé xem phim:</h2>
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          dateFormat='dd/MM/yyyy'
          minDate={currentDate}
          maxDate={maxDate}
        />
        <p>Ngày đã chọn: {selectedDate && selectedDate.toLocaleDateString()}</p>
      </div>
    </section>
  );
};

const Ticket = () => {
  return (
    <>{ReactDOM.createPortal(<Booking />, document.getElementById('ticket'))}</>
  );
};

export default Ticket;
