import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import styles from './Ticket.module.css';

const Booking = () => {
  const [selectedDate, setSelectedDate] = useState(null);

  const handleDateChange = event => {
    const selectedValue = event.target.value;
    setSelectedDate(selectedValue !== 'none' ? selectedValue : null);
  };

  const currentDate = new Date();

  const dateOptions = Array.from({ length: 7 }, (_, index) => {
    const date = new Date(currentDate);
    date.setDate(currentDate.getDate() + index);
    return date;
  });

  return (
    <section className={styles.session}>
      <div>
        <ul>
          {dateOptions.map((date, index) => (
            <li key={index}>
              <div className={`${styles['date']} ${styles['choosed']}`}>
                <strong>{date.getDate().toString().padStart(2, '0')}</strong>
                <span>{date.getMonth() + 1}</span>
                <span>
                  {(() => {
                    switch (date.getDay()) {
                      case 0:
                        return <p>Sun</p>;
                      case 1:
                        return <p>Mon</p>;
                      case 2:
                        return <p>Tue</p>;
                      case 3:
                        return <p>Wed</p>;
                      case 4:
                        return <p>Thu</p>;
                      case 5:
                        return <p>Fri</p>;
                      case 6:
                        return <p>Sat</p>;
                      default:
                        return null;
                    }
                  })()}
                </span>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
};

const Ticket = () => {
  return (
    <>{ReactDOM.createPortal(<Booking />, document.getElementById('ticket'))}</>
  );
};

export default Ticket;
