import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import styles from './Ticket.module.css';

const Booking = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [bookedSeat, setBookedSeat] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState([]);
  // const [seats, setSeats] = useState(
  //   Array(20)
  //     .fill()
  //     .map((_, index) => index + 1)
  // );

  const fellTicketHandler = async () => {
    const response = await fetch('http://localhost:8080/ticket/get');

    if (!response.ok) {
      //
    }

    const data = await response.json();
    setBookedTicket(data);
  };

  useEffect(() => {
    fellTicketHandler();
  }, []);

  console.log(bookedTicket);

  const currentDate = new Date();

  const dateOptions = Array.from({ length: 7 }, (_, index) => {
    const date = new Date(currentDate);
    date.setDate(currentDate.getDate() + index);
    return date;
  });

  const onSelectedHandler = date => {
    setSelectedDate(date);
  };

  return (
    <section className={styles.session}>
      <div>
        <ul>
          {dateOptions.map((date, index) => (
            <li
              key={index}
              className={
                selectedDate &&
                date &&
                selectedDate.getDate() === date.getDate()
                  ? styles['selected']
                  : ''
              }
              onClick={() => onSelectedHandler(date)}
            >
              <div className={styles['date']}>
                <strong>{date.getDate().toString().padStart(2, '0')}</strong>
                <span>{date.getMonth() + 1}</span>
                <span>
                  {(() => {
                    switch (date.getDay()) {
                      case 0:
                        return <p>Sun</p>;
                      case 1:
                        return <p>Mon</p>;
                      case 2:
                        return <p>Tue</p>;
                      case 3:
                        return <p>Wed</p>;
                      case 4:
                        return <p>Thu</p>;
                      case 5:
                        return <p>Fri</p>;
                      case 6:
                        return <p>Sat</p>;
                      default:
                        return null;
                    }
                  })()}
                </span>
              </div>
            </li>
          ))}
        </ul>
        <div className={styles.seats}>
          <ul>
            <li>01</li>
            <li>02</li>
            <li>03</li>
            <li>04</li>
            <li>05</li>
            <li>06</li>
            <li>07</li>
            <li>08</li>
          </ul>
          <ul>
            <li>09</li>
            <li>10</li>
            <li>11</li>
            <li>12</li>
            <li>13</li>
            <li>14</li>
            <li>15</li>
            <li>16</li>
          </ul>
          <ul>
            <li>17</li>
            <li>18</li>
            <li>19</li>
            <li>20</li>
            <li>21</li>
            <li>22</li>
            <li>23</li>
            <li>24</li>
          </ul>
          <ul>
            <li>25</li>
            <li>26</li>
            <li>27</li>
            <li>28</li>
            <li>29</li>
            <li>30</li>
            <li>31</li>
            <li>32</li>
          </ul>
          <ul>
            <li>33</li>
            <li>34</li>
            <li>35</li>
            <li>36</li>
            <li>37</li>
            <li>38</li>
            <li>39</li>
            <li>40</li>
          </ul>
          <ul>
            <li>41</li>
            <li>42</li>
            <li>43</li>
            <li>44</li>
            <li>45</li>
            <li>46</li>
            <li>47</li>
            <li>48</li>
          </ul>
        </div>
        <form className={styles.button}>
          <button type='button' onClick={onCancelHandler}>
            Cancel
          </button>
          <button type='submit' onSubmit={onSubmitHandler}>
            Submit
          </button>
        </form>
      </div>
    </section>
  );
};

const Ticket = () => {
  return (
    <>{ReactDOM.createPortal(<Booking />, document.getElementById('ticket'))}</>
  );
};

export default Ticket;
