import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import styles from './Ticket.module.css';

const Booking = () => {
  const [selectedDate, setSelectedDate] = useState(null);

  const handleDateChange = date => {
    setSelectedDate(date);
  };

  const currentDate = new Date();

  const maxDate = new Date();
  maxDate.setDate(currentDate.getDate() + 6);

  return (
    <section className={styles.session}>
      <div>
        <h2>Chọn ngày đặt vé xem phim:</h2>
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          dateFormat='dd/MM/yyyy' // Định dạng ngày tháng
          minDate={currentDate} // Ngày tối thiểu (không cho chọn ngày trong quá khứ)
          maxDate={maxDate} // Ngày tối đa (ngày hiện tại + 14 ngày)
        />
        <p>Ngày đã chọn: {selectedDate && selectedDate.toLocaleDateString()}</p>
      </div>
    </section>
  );
};

const Ticket = () => {
  return (
    <>{ReactDOM.createPortal(<Booking />, document.getElementById('ticket'))}</>
  );
};

export default Ticket;
